      // Allow images from the same origin, data URIs, and HTTPS
      scriptSrc: ["'self'"],
      // Allow scripts from the same origin
      styleSrc: ["'self'", "'unsafe-inline'"],
      // Allow inline styles (e.g., Bootstrap)
