    }
  }
}));

// Protect against XSS attacks
app.use(xss());

// Apply rate limiting to all requests
var limiter = rateLimit({
  windowMs: 15 * 60 * 1000,
  // 15-minute window
  max: 100,
  // Limit each IP to 100 requests per windowMs
